mapper.update:
UPDATE taskinstance
SET start_time = #{start_time},
    end_time = #{end_time},
    state = #{state}
WHERE taskinstance_id = #{taskinstance_id}

mapper.select:
SELECT * FROM taskinstance


        DATE(start_time) = DATE(#{timestamp})


        AND user_id = #{user_id}


        AND taskinstance_id = #{taskinstance_id}


        AND task_id = #{task_id}
